import {
	GetAccount,
	UpsertAccount,
	UpsertCredential,
} from "../../wailsjs/go/main/App";

export class MastadonWorker {
	/**
	 * Onboard the (pre-validated) user in the database
	 * @param subdomain mastadon server full url
	 * @param meta generated by a service function
	 * @returns true if successful, false otherwise
	 */
	static async onboardUser(
		subdomain: string,
		meta: {
			profile_pic_url: string;
			display_name: string;
			username: string;
			accessToken: string;
			instanceUrl: string;
		}
	) {
		try {
			await UpsertAccount({
				domain: "mastadon",
				subdomain: subdomain,
				username: meta.username,
				password: "",
				verified: true,
			});

			console.log("searching for", "mastadon", subdomain, meta.username);
			const account = await GetAccount("mastadon", subdomain, meta.username);
			console.log(account);
			await UpsertCredential(account, "access_token", meta!.accessToken);
			await UpsertCredential(account, "display_name", meta!.display_name);
			await UpsertCredential(account, "profile_pic_url", meta!.profile_pic_url);
			return {
				success: true,
			};
		} catch (e) {
			return {
				success: false,
				error: "unknown error occured",
			};
		}
	}
}
